import "./App.scss";
import Button from "./components/Button/Button";
import CommentList from "./components/CommentList/CommentList";
import VideosList from "./components/VideosList/VideosList";
import Header from "./components/Header/Header";
import VideoPlayer from "./components/VideoPlayer/VideoPlayer";
import VideoDetails from "./components/VideoDetails/VideoDetails";
import CommentForm from "./components/CommentForm/CommentForm";
import videosListData from "./data/videos.json";
import videoDetails from "./data/video-details.json";
import Avatar from "./components/Avatar/Avatar";

import React, { Component } from "react";

export default class App extends Component {
	state = {
		videoDetails: videoDetails,
		videosListData: videosListData,
		currentVideoIndex: 0,
		currentVideoId: "84e96018-4022-434e-80bf-000ce4cd12b8",
	};

	playVideo = (id) => {
		console.log("clicked");
		// console.log(index);
		console.log(this.state.currentVideoId);
		// this.setState({ currentVideoIndex: index });
		this.setState({ currentVideoId: id });
	};

	render() {
		return (
			<div className="App">
				<Header />
				<VideoPlayer
          videoDetails={
            this.videoDetails.find(video=>video.id)
						// this.currentVideoId
						// 	? this.state.videoDetails[this.state.currentVideoIndex]
						// 	: this.state.videoDetails.find((video) => {
						// 			console.log(video.id, this.currentVideoId);
						// 			return video.id === this.currentVideoId;
						// 	  })
					}
				/>
				<main className="main">
					<VideoDetails
						videoDetails={
							this.state.videoDetails[this.state.currentVideoIndex]
						}
					/>
					<section className="comments">
						<div className="comments__container">
							{/* <img src="" alt="user-icon" /> */}
							<Avatar className={"comments__avatar"} />
							<CommentForm />
							<CommentList
								videoDetail={
									this.state.videoDetails[this.state.currentVideoIndex]
								}
							/>
						</div>
					</section>

					<aside className="videos-list">
						<VideosList
							videosListData={this.state.videosListData}
							currentVideoIndex={this.state.currentVideoIndex}
							handlePlayVideo={this.playVideo}
						/>
					</aside>
				</main>
			</div>
		);
	}
}
