import "./CommentForm.scss";
import Button from "../Button/Button";
import AddCommentLogo from "../../assets/icons/add_comment.svg";
import React, { Component } from "react";

export default class CommentForm extends Component {
	onSubmit(e) {
		// const red = {border: 2px solid red}
		e.preventDefault();
		console.log(e);
		if (!e.target.children[0].children[1].value) {
			e.target.children[0].children[1].style = { border: "2px solid red" };
		}
	}

	render() {
		return (
			<form onSubmit={this.onSubmit} action="" className="comments__form">
				<div className="comment__input-wrapper">
					<label htmlFor="comment">
						<p className="comment__input-label">Join the conversation</p>
					</label>
					<textarea
						name="comment"
						className="comment__input input-field"
						placeholder="Add a new comment"
						rows={4}
					></textarea>
				</div>
				<Button
					message="Comment"
					image={AddCommentLogo}
					className="comment__button"
				/>
			</form>
		);
	}
}
